import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";

import HomeIcon from "@material-ui/icons/Home";
import InsertInvitation from "@material-ui/icons/InsertInvitation";
import Mood from "@material-ui/icons/Mood";

import PeopleOutline from "@material-ui/icons/PeopleOutline";
import Badge from "@material-ui/core/Badge";
import LocalGasStation from "@material-ui/icons/LocalGasStation";
import MusicNote from "@material-ui/icons/MusicNote";
import InsertChartOutlined from "@material-ui/icons/InsertChartOutlined";
import MailIcon from "@material-ui/icons/Mail";
import AccountBalanceWallet from "@material-ui/icons/AccountBalanceWallet";
import AccountBalance from "@material-ui/icons/AccountBalance";

import LibraryBooks from "@material-ui/icons/LibraryBooks";

import InsertDriveFile from "@material-ui/icons/InsertDriveFile";
import { BrowserRouter, Link } from "react-router-dom";

import Drawer from "@material-ui/core/Drawer";
import IconButton from "@material-ui/core/IconButton";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import Divider from "@material-ui/core/Divider";

const drawerWidth = 245;

const styles = theme => ({
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
    display: "flex"
  },
  nested: {
    paddingLeft: theme.spacing.unit * 4
  },

  roottest: {
    // paddingTop: theme.spacing.unit * 1,
    // paddingBottom: theme.spacing.unit * 1,
    paddingLeft: theme.spacing(1),
    marginTop: theme.spacing(-1),
    marginLeft: theme.spacing(30),
    //marginRight: theme.spacing.unit * 30,
    align: "center",
    width: "875",
    height: "120"
  },

  rightToolbar: {
    marginLeft: "auto",
    marginRight: -12
  },

  // pushes appBar to the right
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    })
  },

  // Menu button positioning
  menuButton: {
    marginLeft: 12,
    marginRight: 20
  },

  // no visible change
  hide: {
    display: "none"
  },

  // displays drawer and does ___??__
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },

  // keeps content and top bar from not touching the side when the drawer goes out
  drawerPaper: {
    width: drawerWidth
  },

  // ????
  drawerHeader: {
    display: "flex",
    alignItems: "nter",
    padding: "0 8px",
    ...theme.mixins.toolbar,
    justifyContent: "flex-end"
  },

  // content positioning
  content: {
    flexGrow: 1,
    //padding: theme.spacing.unit * 3,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    marginLeft: -drawerWidth
  },

  //push text/content to the right
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    }),
    marginLeft: 0
  }
});

const navStyle = {
  color: "black",
  textDecoration: "none"
};

/* Drawer class */
class NestedList2 extends React.Component {
  state = {
    open: false,
    anchorEl: null
  };

  handleProfileMenuOpen = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleMenuClose = () => {
    this.setState({ anchorEl: null });
  };

  // Open drawer
  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  // Close drawer
  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  //sublist click
  handleClick = () => {
    this.setState(state => ({ open: !state.open }));
  };

  handleClick = () => {
    this.setState(state => ({ open: !state.open }));
  };
  //ListItemIcon makes the icon faded and does the positioning
  render() {
    const { classes, theme } = this.props;
    const { open } = this.state;

    return (
      <div>
        <Drawer
          className={classes.drawer}
          variant="persistent"
          anchor="left"
          open={open}
          classes={{
            paper: classes.drawerPaper
          }}
        >
          {/*Action to close drawer */}
          <IconButton onClick={this.handleDrawerClose}>
            {theme.direction === "ltr" ? (
              /* Close drawer icon */
              <ChevronLeftIcon />
            ) : (
              <ChevronRightIcon />
            )}
          </IconButton>

          <Divider />

          <List component="nav" className={classes.root}>
            <BrowserRouter>
              <Link style={navStyle} to="/blah">
                <ListItem button>
                  <ListItemIcon>
                    <HomeIcon />
                  </ListItemIcon>
                  <ListItemText primary="Home" />
                </ListItem>
              </Link>
            </BrowserRouter>
            <ListItem button>
              <ListItemIcon>
                <InsertInvitation />
              </ListItemIcon>
              <ListItemText primary="Calendar" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <Badge badgeContent={2} color="secondary">
                  <MailIcon />
                </Badge>
              </ListItemIcon>
              <ListItemText primary="Messages" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <Mood />
              </ListItemIcon>
              <ListItemText primary="Clients" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <PeopleOutline />
              </ListItemIcon>
              <ListItemText primary="Team Members" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <MusicNote />
              </ListItemIcon>
              <ListItemText primary="Inventory" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <LocalGasStation />
              </ListItemIcon>
              <ListItemText primary="Mileage" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <AccountBalanceWallet />
              </ListItemIcon>
              <ListItemText primary="Acounts & Invoices" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <AccountBalance />
              </ListItemIcon>
              <ListItemText primary="Payroll" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <InsertChartOutlined />
              </ListItemIcon>
              <ListItemText primary="Reports" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <InsertDriveFile />
              </ListItemIcon>
              <ListItemText primary="Forms" />
            </ListItem>
            <ListItem button>
              <ListItemIcon>
                <LibraryBooks />
              </ListItemIcon>
              <ListItemText primary="Note Templates" />
            </ListItem>
          </List>
        </Drawer>
      </div>
    );
  }
}

NestedList2.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(NestedList2);
