import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import "moment";
import MomentUtils from "@date-io/moment";
import { MuiPickersUtilsProvider, DatePicker } from "@material-ui/pickers";

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const useStyles = makeStyles(theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400
  },
  textField2: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200
  }
}));

export default function OutlinedTextFields() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    newClient: "",
    newTherapist: "",
    newLocation: "",
    newDuration: ""
  });

  // The first commit of Material-UI
  const [selectedDateStart] = React.useState(new Date());
  const [setSelectedDateStart] = React.useState(
    new Date("2019-04-01T21:11:54")
  );
  const [selectedDateEnd, setSelectedDateEnd] = React.useState(
    new Date("2019-05-01T21:11:54")
  );

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  function handleDateChangeStart(date) {
    setSelectedDateStart(date);
  }

  function handleDateChangeEnd(date) {
    setSelectedDateEnd(date);
  }

  return (
    <Grid container direction="column" justify="center" alignItems="flex-start">
      <TextField
        id="standard-select-client"
        select
        label="Client"
        variant="outlined"
        margin="normal"
        className={classes.textField}
        value={values.newClient}
        onChange={handleChange("newClient")}
        SelectProps={{
          MenuProps: {
            className: classes.menu
          }
        }}
      >
        {clients.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        id="standard-select-client"
        select
        label="Therapist"
        variant="outlined"
        margin="normal"
        className={classes.textField}
        value={values.newTherapist}
        onChange={handleChange("newTherapist")}
        SelectProps={{
          MenuProps: {
            className: classes.menu
          }
        }}
      >
        {therapists.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <Grid direction="row">
        <TextField
          id="standard-newLocation"
          label="Location"
          variant="outlined"
          className={classes.textField2}
          value={values.newLocation}
          onChange={handleChange("newLocation")}
          margin="normal"
        />
        <TextField
          id="standard-newDuration"
          label="Duration (minutes)"
          variant="outlined"
          className={classes.textField2}
          value={values.newDuration}
          onChange={handleChange("newDuration")}
          margin="normal"
        />
      </Grid>
      <MuiPickersUtilsProvider utils={MomentUtils}>
        <Grid direction="row">
          <DatePicker
            inputVariant="outlined"
            className={classes.textField2}
            margin="normal"
            id="mui-pickers-date"
            label="Start Date"
            value={selectedDateStart}
            onChange={handleDateChangeStart}
          />
          <DatePicker
            inputVariant="outlined"
            margin="normal"
            className={classes.textField2}
            id="mui-pickers-date"
            label="End Date"
            value={selectedDateEnd}
            onChange={handleDateChangeEnd}
          />
        </Grid>
      </MuiPickersUtilsProvider>
    </Grid>
  );
}
