import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import "moment";
import MomentUtils from "@date-io/moment";
import {
  MuiPickersUtilsProvider,
  DatePicker,
  TimePicker
} from "@material-ui/pickers";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const categories = [
  {
    value: "None",
    label: "None"
  }
];

const reccurOptions = [
  {
    value: "Day",
    label: "Day"
  },

  {
    value: "Week",
    label: "Week"
  },
  {
    value: "Month",
    label: "Month"
  },
  {
    value: "Custom",
    label: "Custom"
  }
];

const useStyles = makeStyles(theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400
  },
  textField2: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 192
  }
}));

export default function OutlinedTextFields() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    newClient: "",
    newTherapist: "",
    newLocation: "",
    newCategory: "",
    newOccurence: ""
  });
  const [state, setState] = React.useState({
    checkedRecurring: true
  });

  // The first commit of Material-UI
  const [selectedDateStart, setSelectedDateStart] = React.useState(
    new Date("2019-04-01T21:11:54")
  );

  const [selectedDateEnd, setSelectedDateEnd] = React.useState(
    new Date("2019-04-01T21:11:54")
  );

  const [selectedTimeStart, setSelectedTimeStart] = React.useState(
    new Date("2019-04-01T15:11:54")
  );
  const [selectedTimeEnd, setSelectedTimeEnd] = React.useState(
    new Date("2019-05-01T21:11:54")
  );

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  const handleChangeOccurence = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  const handleChangeCheckBox = name => event => {
    setState({ ...state, [name]: event.target.checked });
  };

  function handleDateChangeStart(date) {
    setSelectedDateStart(date);
  }

  function handleDateChangeEnd(date) {
    setSelectedDateEnd(date);
  }

  function handleTimeChangeStart(date) {
    setSelectedTimeStart(date);
  }

  function handleTimeChangeEnd(date) {
    setSelectedTimeEnd(date);
  }

  return (
    <Grid container direction="column" justify="center" alignItems="flex-start">
      <TextField
        required
        id="standard-select-client"
        select
        label="Client"
        variant="outlined"
        margin="normal"
        className={classes.textField}
        value={values.newClient}
        onChange={handleChange("newClient")}
        SelectProps={{
          MenuProps: {
            className: classes.menu
          }
        }}
      >
        {clients.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        required
        id="standard-select-client"
        select
        label="Therapist"
        variant="outlined"
        margin="normal"
        className={classes.textField}
        value={values.newTherapist}
        onChange={handleChange("newTherapist")}
        SelectProps={{
          MenuProps: {
            className: classes.menu
          }
        }}
      >
        {therapists.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <Grid direction="row">
        <TextField
          id="standard-newLocation"
          label="Location"
          variant="outlined"
          className={classes.textField2}
          value={values.newLocation}
          onChange={handleChange("newLocation")}
          margin="normal"
        />
        <TextField
          id="standard-select-client"
          select
          label="Category"
          variant="outlined"
          margin="normal"
          className={classes.textField2}
          value={values.newCategory}
          onChange={handleChange("newCategory")}
          SelectProps={{
            MenuProps: {
              className: classes.menu
            }
          }}
        >
          {categories.map(option => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      </Grid>
      <MuiPickersUtilsProvider utils={MomentUtils}>
        <Grid direction="row">
          <DatePicker
            inputVariant="outlined"
            className={classes.textField2}
            margin="normal"
            id="mui-pickers-date"
            label="Start Date"
            value={selectedDateStart}
            onChange={handleDateChangeStart}
          />
          <TimePicker
            inputVariant="outlined"
            className={classes.textField2}
            margin="normal"
            id="mui-pickers-time"
            label="Start Time"
            value={selectedTimeStart}
            onChange={handleTimeChangeStart}
          />
        </Grid>
      </MuiPickersUtilsProvider>
      <MuiPickersUtilsProvider utils={MomentUtils}>
        <Grid direction="row">
          <DatePicker
            inputVariant="outlined"
            margin="normal"
            className={classes.textField2}
            id="mui-pickers-date"
            label="End Date"
            value={selectedDateEnd}
            onChange={handleDateChangeEnd}
          />
          <TimePicker
            inputVariant="outlined"
            className={classes.textField2}
            margin="normal"
            id="mui-pickers-time"
            label="End Time"
            value={selectedTimeEnd}
            onChange={handleTimeChangeEnd}
          />
        </Grid>
      </MuiPickersUtilsProvider>
      <FormGroup row>
        <FormControlLabel
          control={
            <Checkbox
              checked={state.checkedRecurring}
              onChange={handleChangeCheckBox("checkedRecurring")}
              value="checkedRecurring"
              color="primary"
            />
          }
          label="Recurring"
        />
      </FormGroup>
      <TextField
        id="standard-select-client"
        select
        label="Reccur Every:"
        variant="outlined"
        margin="normal"
        className={classes.textField2}
        value={values.newOccurence}
        onChange={handleChangeOccurence("newOccurence")}
        SelectProps={{
          MenuProps: {
            className: classes.menu
          }
        }}
      >
        {reccurOptions.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
    </Grid>
  );
}
