import React, { Component } from "react";
//import { render } from "react-dom";
import events from "./events";
import BigCalendar from "react-big-calendar";
import moment from "moment";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import NewEventDialog from "./newEventDialog";
//import NewEventDialog2 from "./newEventDialog2";
import ExistingEventDialog from "./existingEventDialog";
import "react-big-calendar/lib/css/react-big-calendar.css";
import MomentUtils from "@date-io/moment";
import {
  MuiPickersUtilsProvider,
  DatePicker,
  TimePicker
} from "@material-ui/pickers";

import axios from "axios";
//tes

const localizer = BigCalendar.momentLocalizer(moment);
const propTypes = {};

const styles = theme => ({
  state: {
    open: false,
    openV: false
  },

  root: {
    padding: theme.spacing(10, 10),
    height: "100%",
    width: "90%"
  },
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400
  },
  textField2: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 192
  },
  dense: {
    marginTop: 16
  },
  menu: {
    width: 200
  }
});

const newBillTypes = [
  {
    value: "Billable",
    label: "Billable"
  },

  {
    value: "Non-billable",
    label: "Non-billable"
  }
];

const newClientTypes = [
  {
    value: "Individual",
    label: "Individual"
  },

  {
    value: "Facility",
    label: "Facility"
  }
];

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const categories = [
  {
    value: "None",
    label: "None"
  }
];

class ReactCalendarBase extends Component {
  constructor(...args) {
    super(...args);

    this.state = {
      cal_events: [
        //State is updated via componentDidMount
      ],
      data: [],
      //id: 0,
      newBillType: null,
      newClientType: null,
      // title: null,
      newClient: null,
      newTherapist: null,
      newLocation: null,
      newCategory: null,
      newStartDate: null,
      newStartTime: null,
      newEndDate: null,
      newEndTime: null,
      information: null,
      intervalIsSet: false,
      idToDelete: null,
      idToUpdate: null,
      objectToUpdate: null,
      selectedDate: new Date()
    };
  }

  convertDate = date => {
    return moment.utc(date).toDate();
  };

  componentDidMount() {
    axios
      .get("http://localhost:5000/events")
      .then(response => {
        console.log(response.data);
        let appointments = response.data;

        for (let i = 0; i < appointments.length; i++) {
          appointments[i].start = this.convertDate(appointments[i].start);
          appointments[i].end = this.convertDate(appointments[i].end);
        }

        this.setState({
          cal_events: appointments
        });
      })
      .catch(function(error) {
        console.log(error);
      });
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  // never let a process live forever
  // always kill a process everytime we are done using it
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  // just a note, here, in the front end, we use the id key of our data object
  // in order to identify which we want to Update or delete.
  // for our back end, we use the object id assigned by MongoDB to modify
  // data base entries

  // our first get method that uses our backend api to
  // fetch data from our data base
  getDataFromDb = () => {
    fetch("http://localhost:5000/events")
      .then(data => data.json())
      .then(res => this.setState({ data: res.data }));
  };

  // our put method that uses our backend api
  // to create new query into our data base
  /*
  //this is being commented out because the same thing is already being done at onSubmit
  putDataToDB = (
    newBillType,
    newClientType,
    title,
    newClient,
    newTherapist,
    newLocation,
    newCategory
  ) => {
    let currentIds = this.state.data.map(data => data.id);
    let idToBeAdded = 0;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post("http://localhost:5000/api/putData2", {
      id: idToBeAdded,
      newBillType: newBillType,
      newClientType: newClientType,
      title: title,
      newClient: newClient,
      newTherapist: newTherapist,
      newLocation: newLocation,
      newCategory: newCategory
      //start: start,
      //end: end,
      //newClient: newClient,
      //newTherapist: newTherapist
    });
  };
*/

  onSubmit(e) {
    //e.preventDefault();
    const obj = {
      //title: this.state.title,
      newBillType: this.state.newBillType,
      newClientType: this.state.newClientType,
      newTherapist: this.state.newTherapist,
      newLocation: this.state.newLocation,
      newCategory: this.state.newCategory,
      newClient: this.state.newClient,
      selectedDate: this.state.selectedDate
    };
    axios
      .post("http://localhost:5000/putData2", obj)
      .then(res => console.log(res.data));
    /* this will clear everything after saving+closing */

    this.setState({
      newBillType: "",
      newClientType: "",
      title: "",
      newClient: "",
      newTherapist: "",
      newLocation: "",
      newCategory: "",
      selectedDate: ""
    });
  }

  /* show new event dialog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close new event dialog box */
  handleClose = () => {
    this.setState({
      open: false
      /*
      //this will set the values to blank once the window is closed
      newBillType: "",
      newClientType: "",
      title: "",
      newClient: "",
      newTherapist: "",
      newLocation: "",
      newCategory: ""
*/
    });
  };

  /* show existing event dialog box */
  handleClickOpen2 = () => {
    this.setState({ openV: true });
  };

  /* close existing event dialog box */
  handleClose2 = () => {
    this.setState({ openV: false });
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;
    //const classes = withStyles();
    const { cal_events, selectedDate } = this.state;

    return (
      <div style={{ height: 1000 }}>
        <BigCalendar
          className={classes.root}
          selectable
          localizer={localizer}
          events={cal_events}
          //events={events}
          views={["month", "week", "day"]}
          defaultDate={new Date(2019, 3, 1)}
          onSelectEvent={this.handleClickOpen2}
          onSelectSlot={this.handleClickOpen}
        />

        {/* existing dialog */}
        <Dialog open={this.state.openV} onClose={this.handleClose2}>
          <DialogContent>
            <ExistingEventDialog />
          </DialogContent>
          <DialogActions>
