                  </MenuItem>
                ))}
              </TextField>

              <TextField
                required
                id="client_type"
                select
                label="Client Type"
                className={classes.textField}
                value={this.state.newClientType}
                onChange={e => this.setState({ newClientType: e.target.value })}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {newClientTypes.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="newClient"
                select
                label="Client"
                className={classes.textField}
                value={this.state.newClient}
                onChange={e => this.setState({ newClient: e.target.value })}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {clients.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="newTherapist"
                select
                label="Therapist"
                className={classes.textField}
                value={this.state.newTherapist}
                onChange={e => this.setState({ newTherapist: e.target.value })}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {therapists.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="newLocation"
                label="Location "
                className={classes.textField2}
                value={this.state.newLocation}
                onChange={e => this.setState({ newLocation: e.target.value })}
                margin="normal"
                variant="outlined"
              />
              <TextField
                required
                id="newCategory"
                select
                label="Category"
                className={classes.textField2}
                value={this.state.newCategory}
                onChange={e => this.setState({ newCategory: e.target.value })}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {categories.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <MuiPickersUtilsProvider utils={MomentUtils}>
                <Grid container className={classes.grid} justify="space-around">
                  <DatePicker
                    margin="normal"
                    label="Date picker"
                    value={selectedDate}
                    onChange={this.handleDateChange}
                  />
                  <TimePicker
                    margin="normal"
                    label="Time picker"
                    value={selectedDate}
                    onChange={this.handleDateChange}
                  />
                </Grid>
              </MuiPickersUtilsProvider>
            </DialogContent>
            <DialogActions>
              <Button onClick={this.handleClose} color="primary">
                Cancel
              </Button>
              <Button
                onClick={() => {
                  this.onSubmit(
                    //this.state.title,
                    this.state.newBillType,
                    this.state.newClientType,
                    this.state.newClient,
                    this.state.thereapist,
                    this.state.newLocation,
                    this.state.newCategory,
                    this.state.selectedDate
                  );
                  this.handleClose();
                }}
                color="primary"
              >
                Save & Close
              </Button>
            </DialogActions>
          </form>
        </Dialog>
      </div>
    );
  }
}

