import React, { Component } from "react";
//import { render } from "react-dom";
import events from "./events";
import BigCalendar from "react-big-calendar";
import moment from "moment";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";

import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = BigCalendar.momentLocalizer(moment);
const propTypes = {};

const styles = theme => ({
  state: {
    open: false
  },

  textField2: {
    //marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit
    //width: 100
  }
});

class ReactCalendarBase extends Component {
  constructor(...args) {
    super(...args);

    this.state = { events };
  }

  /* show assign client diaglog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close assign client diaglog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  handleSelect = ({ start, end }) => {
    const title = window.prompt("New Event name");
    if (title)
      this.setState({
        events: [
          ...this.state.events,
          {
            start,
            end,
            title
          }
        ]
      });
  };

  render() {
    const { classes } = this.props;

    return (
      <div style={{ height: 600 }}>
        <BigCalendar
          selectable
          localizer={localizer}
          events={events}
          views={["month", "week", "day"]}
          defaultDate={new Date(2019, 3, 1)}
          onSelectEvent={event => alert(event.title, event.start)}
          onSelectSlot={this.handleClickOpen}
        />
        <Dialog open={this.state.open} onClose={this.handleClose}>
          <DialogTitle id="form-dialog-title">Transaction Details</DialogTitle>
          <DialogContent>
            <TextField
              id="standard-date"
              label="Date"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.date}
              //onChange={this.handleChange("date")}
              margin="normal"
            />
            <TextField
              id="standard-payor"
              label="Payor"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.payor}
              //onChange={this.handleChange("payor")}
              margin="normal"
            />
            <TextField
              id="standard-amount"
              label="Amount"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.amount}
              //onChange={this.handleChange("amount")}
              margin="normal"
            />
            <TextField
              id="standard-paymentMethod"
              label="Payment Method"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.paymentMethod}
              //onChange={this.handleChange("paymentMethod")}
              margin="normal"
            />
            <TextField
              id="standard-charges"
              label="Charges"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.charges}
              //onChange={this.handleChange("charges")}
              margin="normal"
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={this.handleClose} color="primary">
              Save
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

ReactCalendarBase.propTypes = propTypes;

//export default ReactCalendarBase;
export default withStyles(styles)(ReactCalendarBase);
