import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import classNames from "classnames";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import PermIdentity from "@material-ui/icons/PermIdentity";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Grid from "@material-ui/core/Grid";

const styles = theme => ({
  root: {
    // paddingTop: theme.spacing.unit * 2,
    //paddingBottom: theme.spacing.unit * 2,
    marginTop: theme.spacing.unit * 5
    // marginLeft: theme.spacing.unit * 33.5,
    //align: "center",
    // width: "175"
  },
  button: {
    //margin: theme.spacing.unit,
    //width: 160
  },
  leftIcon: {
    marginRight: theme.spacing.unit
  },
  rightIcon: {
    marginLeft: theme.spacing.unit
  },

  floatButton: {
    marginTop: theme.spacing(4),
    margin: theme.spacing.unit,
    /* this is text color */ color: theme.palette.getContrastText("#b2dfdb"),
    backgroundColor: "#b2dfdb",
    "&:hover": {
      backgroundColor: "#80cbc4"
    }
  },

  dialogTitle: {
    marginBottom: theme.spacing.unit
  },
  iconSmall: {
    fontSize: 20
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 300
  },
  menu: {
    width: 200
  }
});

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const sessionType = [
  {
    value: "Lessons",
    label: "Lessons"
  },

  {
    value: "Therapy",
    label: "Therapy"
  }
];

class AssignedClientActions extends React.Component {
  state = {
    member: "Harry Potter",
    open: false,
    client: "",
    fileName: "",
    fileDescription: "",
    type: ""
  };

  /* change of team member dropdown */
  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  /* show assign client diaglog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close assign client diaglog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  render() {
    const { classes } = this.props;

    return (
      <div>
        <Grid
          container
          direction="row-reverse"
          justify="center"
          alignItems="center"
        >
          <Paper className={classes.root} elevation={7}>
            {/* Assign client button */}
            <Button
              variant="contained"
              onClick={this.handleClickOpen}
              className={classes.floatButton}
            >
              <AddIcon />
              Upload File
            </Button>
            <Dialog open={this.state.open} onClose={this.handleClose}>
              <DialogTitle id="form-dialog-title">Upload File</DialogTitle>

              <DialogContent>
                <input
                  accept=".pdf,.doc,.docx"
                  className={classes.input}
                  id="contained-button-file"
                  //multiple
                  type="file"
                />

                <TextField
                  id="standard-fileName"
                  required
                  label="File Name"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.fileName}
                  onChange={this.handleChange("fileName")}
                  margin="normal"
                />
                <TextField
                  id="standard-description"
                  label="Description"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.fileDescription}
                  onChange={this.handleChange("fileDescription")}
                  margin="normal"
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={this.handleClose} color="primary">
                  Cancel
                </Button>
                <Button onClick={this.handleClose} color="primary">
                  Upload
                </Button>
              </DialogActions>
            </Dialog>
          </Paper>
        </Grid>
      </div>
    );
  }
}

AssignedClientActions.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(AssignedClientActions);
