import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import classNames from "classnames";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import PermIdentity from "@material-ui/icons/PermIdentity";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";

import axios from "axios";

const styles = theme => ({
  root: {
    paddingTop: theme.spacing.unit * 2,
    paddingBottom: theme.spacing.unit * 2,
    marginTop: theme.spacing.unit * 5,
    marginLeft: theme.spacing.unit * 33.5,
    align: "center",
    width: "175"
  },
  button: {
    margin: theme.spacing.unit,
    width: 160
  },
  leftIcon: {
    marginRight: theme.spacing.unit
  },
  rightIcon: {
    marginLeft: theme.spacing.unit
  },
  dialogTitle: {
    marginBottom: theme.spacing.unit
  },
  iconSmall: {
    fontSize: 20
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 300
  },
  menu: {
    width: 200
  }
});

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const sessionType = [
  {
    value: "Lessons",
    label: "Lessons"
  },

  {
    value: "Therapy",
    label: "Therapy"
  }
];

class AssignedClientActions extends React.Component {
  state = {
    member: "Harry Potter",
    clientData: [],
    clients: [],
    selectedClient: "",
    open: false,
    client: "",
    cost: "",
    length: "",
    type: ""
  };

  /* change of team member dropdown */
  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  /* show assign client diaglog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close assign client diaglog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  componentDidMount() {
    axios
      .get("http://localhost:5000/clientlist")
      .then(response => {
        console.log("Got client data!");
        console.log(response.data);
        let teamsFromApi = response.map(client => {
          return { value: client, display: client };
        });
        this.setState({
          clients: [
            { value: "", display: "(Select your favourite team)" }
          ].concat(teamsFromApi)
        });
        /*
        this.setState({
          clientData: response.data
        });
        */
      })
      .catch(function(error) {
        console.log(error);
      });
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
      console.log("Client interval set!");
    }
  }

  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
      console.log("Unmounted from client data!");
    }
  }

  render() {
    const { classes } = this.props;
    const { clientData } = this.state;

    return (
      <div>
        <Paper className={classes.root} elevation={7}>
          {/* Assign client button */}
          <Button
            variant="contained"
            onClick={this.handleClickOpen}
            className={classes.button}
          >
            <AddIcon
              className={classNames(classes.leftIcon, classes.iconSmall)}
            />
            Assign Client
          </Button>
          <Dialog open={this.state.open} onClose={this.handleClose}>
            <DialogTitle id="form-dialog-title">Assign Client</DialogTitle>
            <DialogContent>
              <TextField
                id="standard-select-client"
                select
                label="Clients"
                variant="outlined"
                margin="normal"
                className={classes.textField}
                value={this.state.selectedClient}
                onChange={this.handleChange("client")}
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {clients.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                id="standard-select-sessionType"
                select
                label="Session Type"
                variant="outlined"
                margin="normal"
                className={classes.textField}
                value={this.state.type}
                onChange={this.handleChange("type")}
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {sessionType.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                id="standard-cost"
                label="Session Cost (dollars)"
                variant="outlined"
                className={classes.textField}
                value={this.state.cost}
                onChange={this.handleChange("cost")}
                margin="normal"
              />
              <TextField
                id="standard-length"
                label="Session Length (minutes)"
                variant="outlined"
                className={classes.textField}
                value={this.state.length}
                onChange={this.handleChange("length")}
                margin="normal"
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={this.handleClose} color="primary">
                Cancel
              </Button>
              <Button onClick={this.handleClose} color="primary">
                Save
              </Button>
            </DialogActions>
          </Dialog>
        </Paper>
      </div>
    );
  }
}

AssignedClientActions.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(AssignedClientActions);
