import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import classNames from "classnames";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import EmailIcon from "@material-ui/icons/Email";
import FilterIcon from "@material-ui/icons/FilterList";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import FormControl from "@material-ui/core/FormControl";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import VisibilityIcon from "@material-ui/icons/Visibility";

const styles = theme => ({
  root: {
    paddingTop: theme.spacing(2),
    paddingBottom: theme.spacing(2),
    marginTop: theme.spacing(5),
    marginLeft: theme.spacing(65),
    align: "center",
    width: "19.5%"
  },
  button: {
    margin: theme.spacing.unit
  },
  leftIcon: {
    marginRight: theme.spacing.unit
  },
  rightIcon: {
    marginLeft: theme.spacing.unit
  },
  iconSmall: {
    fontSize: 20
  }
});

class teamMemberActions extends React.Component {
  state = {
    open: false,
    name: true,
    phone: true,
    email: true,
    npi: true,
    role: true
  };

  /* change of team member dropdown */
  handleChange = name => event => {
    this.setState({ [name]: event.target.checked });
  };

  /* show assign client diaglog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close assign client diaglog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  render() {
    const { classes } = this.props;
    const { name, email, phone, npi, role } = this.state;

    return (
      <div>
        <Paper className={classes.root} elevation={7}>
          <Button variant="contained" className={classes.button}>
            <AddIcon
              className={classNames(classes.leftIcon, classes.iconSmall)}
            />
            Add New
          </Button>
          {/*
          <Button
            variant="contained"
            className={classes.button}
            onClick={this.handleClickOpen}
          >

            <VisibilityIcon
              className={classNames(classes.leftIcon, classes.iconSmall)}
            />
            Show
          </Button>
            */}

          {/* 'Show more' dialog box */}
          <Dialog open={this.state.open} onClose={this.handleClose}>
            <DialogTitle id="form-dialog-title">Show</DialogTitle>
            <DialogContent>
              <FormControl component="fieldset" className={classes.formControl}>
                <FormGroup>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={name}
                        onChange={this.handleChange("name")}
                        value="name"
                      />
                    }
                    label="Name"
                  />
                </FormGroup>
                <FormGroup>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={email}
                        onChange={this.handleChange("email")}
                        value="email"
                      />
                    }
                    label="Email"
                  />
                </FormGroup>
                <FormGroup>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={phone}
                        onChange={this.handleChange("phone")}
                        value="phone"
                      />
                    }
                    label="Phone"
                  />
                </FormGroup>
                <FormGroup>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={npi}
                        onChange={this.handleChange("npi")}
                        value="npi"
                      />
                    }
                    label="NPI"
                  />
                </FormGroup>
                <FormGroup>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={role}
                        onChange={this.handleChange("role")}
                        value="role"
                      />
                    }
                    label="Role"
                  />
                </FormGroup>
              </FormControl>
            </DialogContent>
          </Dialog>
        </Paper>
      </div>
    );
  }
}

teamMemberActions.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(teamMemberActions);
