import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import deepPurple from "@material-ui/core/colors/deepPurple";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import green from "@material-ui/core/colors/green";
import red from "@material-ui/core/colors/red";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Checkbox from "@material-ui/core/Checkbox";
import AppBar from "@material-ui/core/AppBar";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import AssignedClientsTable from "../Tables/backEndAssignedClientsTable";
import TeamMemFilesTable from "../Tables/teamMemFilesTable";
import AssignedClientsActions from "../Actions/asssignedClientsActions";
import TeamMemberInfo from "../teamMember/teamMemberInfo";
import MemberUploadAction from "../Actions/memberUploadAction";
import AdminPrivliges from "../teamMember/adminPrivliges";
import TherapistPrivliges from "../teamMember/therapistPrivliges";
import MemberAvatar from "../teamMember/memberAvatar";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Container from "@material-ui/core/Container";
import Cyan from "@material-ui/core/colors/cyan";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import AddIcon from "@material-ui/icons/Add";
import classNames from "classnames";

import axios from "axios";

const styles = theme => ({
  root: {
    //paddingTop: theme.spacing.unit * 1,
    // paddingBottom: theme.spacing.unit * 1,
    // paddingLeft: theme.spacing.unit,
    //marginTop: theme.spacing.unit * 15,
    //marginLeft: theme.spacing.unit * 22,
    //marginRight: theme.spacing.unit * 22
    align: "center",
    //width: "66%",
    height: "130"
  },

  toggle: {
    paddingRight: theme.spacing.unit * 30
  },

  marg: {
    marginLeft: theme.spacing.unit,
    marginBottom: theme.spacing.unit
  },

  purpleAvatar: {
    margin: 10,
    color: "#fff",
    backgroundColor: deepPurple[500],
    width: 100,
    height: 100,
    fontSize: 40,
    marginBottom: theme.spacing.unit * -28,
    marginRight: theme.spacing.unit * 18
  },

  colorSwitchBase: {
    color: red[300],
    "&$colorChecked": {
      color: green[500],
      "& + $colorBar": {
        backgroundColor: green[500]
      }
    }
  },
  colorBar: {},
  colorChecked: {},

  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    marginBottom: theme.spacing.unit,
    width: 300
  },
  menu: {
    width: 200
  },

  checked: {},

  root2: {
    width: "100%",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: Cyan[800]
  },
  infoRoot: {
    marginBottom: theme.spacing.unit * 2
  }
});

const members = [
  {
    value: "Jane Doe",
    label: "Jane Doe"
  },

  {
    value: "Hermione Granger",
    label: "Hermione Granger"
  },
  {
    value: "Harry Potter",
    label: "Harry Potter"
  }
];

const titles = [
  {
    value: "",
    label: ""
  },

  {
    value: "Dr.",
    label: "Dr."
  },

  {
    value: "Miss",
    label: "Miss"
  },

  {
    value: "Mr.",
    label: "Mr."
  },
  {
    value: "Mrs.",
    label: "Mrs."
  },
  {
    value: "Ms.",
    label: "Ms."
  },
  {
    value: "Mx.",
    label: "Mx."
  }
];

class TopPanel extends React.Component {
  state = {
    checkedA: true,
    member: "Harry Potter",
    open: false,
    client: "",
    cost: "",
    length: "",
    type: "",
    checkedAdmin: true,
    checkedThera: true,
    checkedIntern: false,
    tabValue: 0,
    //client info
    firstName: "Harry",
    lastName: "Potter",
    email: "harry@hogwarts.com",
    title: "Mr.",
    currentPassword: "test123",
    confirmPassword: "test123",
    phone: "123-456-1111",
    address: "713 Hogwarts Lane",
    primaryLocation: "Diagon Alley",
    npi: "731890",
    city: "London",
    zipCode: "77777",
    multiline: "Controlled",
    // privliges
    invoice: false,
    allCalendar: false,
    payment: true,
    editOtherSched: false,
    viewOtherSched: false,
    editOtherInfo: false,
    viewOtherInfo: false,
    addClient: true,
    recordAttendance: true,
    viewNote: true,
    viewClient: false,
    kpis: true,
    viewReport: false,
    viewCalendar: true,
    viewOwnClient: true
  };

  componentDidMount() {
    axios
      .get("http://localhost:5000/members")
      .then(response => {
        console.log("Got therapist data data!");
        console.log(response.data);
        this.setState({
          therapistData: response.data
        });
      })
      .then(response2 => {
        return axios.get("http://localhost:5000/allclients").then(response2 => {
          console.log("Got client data!");
          console.log(response2.data);
          this.setState({
            clientData: response2.data
          });
        });
      })
      .catch(function(error) {
        console.log(error);
      });

    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
      console.log("Data interval set!");
    }
  }

  // kills a process everytime we are done using it
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
      console.log("Unmounted from client data!");
    }
  }

  handleChange = name => event => {
    this.setState({ [name]: event.target.checked });
  };

  /* change of team member dropdown */
  handleChange2 = name => event => {
    this.setState({ [name]: event.target.value });
  };

  // change of check boxes
  handleChange3 = name => event => {
    this.setState({ [name]: event.target.checked });
  };

  handleChangeTab = (event, tabValue) => {
    this.setState({ tabValue });
  };

  render() {
    const { classes } = this.props;
    const { tabValue, clientData } = this.state;

    return (
      <div>
        <Container maxWidth="lg">
          {/* Avatar */}
          <Grid container justify="center" alignItems="center">
            <MemberAvatar />
          </Grid>
          <Paper /* className={classes.root} */ elevation={1}>
            <FormGroup row>
              <FormControlLabel
                control={
                  <Switch
                    checked={this.state.checkedA}
                    onChange={this.handleChange("checkedA")}
                    value="checkedA"
                    marginLeft="theme.spacing.unit * 20"
                    className={classes.marg}
                    classes={{
                      switchBase: classes.colorSwitchBase,
                      checked: classes.colorChecked,
                      bar: classes.colorBar
                    }}
                  />
                }
                label="Active"
              />
            </FormGroup>

            {/* member drop down */}
            <Grid container row justify="space-between">
              <TextField
                id="standard-select-member"
                select
                label="Team Member"
                variant="outlined"
                className={classes.textField}
                value={this.state.member}
                onChange={this.handleChange2("member")}
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {members.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>

              <FormGroup row>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={this.state.checkedAdmin}
                      onChange={this.handleChange("checkedAdmin")}
                      value="checkedAdmin"
                      color="default"
                    />
                  }
                  label="Administrator"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={this.state.checkedThera}
                      onChange={this.handleChange("checkedThera")}
                      value="checkedThera"
                      color="default"
                    />
                  }
                  label="Therapist"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={this.state.checkedIntern}
                      onChange={this.handleChange("checkedIntern")}
                      value="checkedIntern"
                      color="default"
                    />
                  }
                  label="Intern"
                />
              </FormGroup>
            </Grid>
          </Paper>

          <AppBar className={classes.root2} position="static">
            <Tabs value={tabValue} onChange={this.handleChangeTab}>
              <Tab label="Team Member Information" />
              <Tab label="Assigned Clients" />
              <Tab label="Message History" />
              <Tab label="Files" />
              <Tab label="Privliges" />
            </Tabs>
          </AppBar>

          {tabValue === 0 && (
            <form
              /* className={classes.container} */ noValidate
              autoComplete="off"
            >
              <Paper className={classes.root} elevation={1}>
                <Grid container justify="center" alignItems="center">
                  <Grid direction="column">
                    <TextField
                      id="standard-select-title"
                      select
                      label="Title"
                      variant="outlined"
                      className={classes.textField}
                      value={this.state.title}
                      onChange={this.handleChange("title")}
                      SelectProps={{
                        MenuProps: {
                          className: classes.menu
                        }
                      }}
                    >
                      {titles.map(option => (
                        <MenuItem key={option.value} value={option.value}>
                          {option.label}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      id="standard-phone"
                      variant="outlined"
                      label="Phone Number"
                      className={classes.textField}
                      value={this.state.phone}
                      onChange={this.handleChange("phone")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      required
                      id="standard-firstName"
                      label="First Name"
                      variant="outlined"
                      className={classes.textField}
                      value={this.state.firstName}
                      onChange={this.handleChange("firstName")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      required
                      id="standard-email"
                      variant="outlined"
                      label="Email"
                      className={classes.textField}
                      value={this.state.email}
                      onChange={this.handleChange("email")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      required
                      id="standard-lastNamename"
                      label="Last Name"
                      variant="outlined"
                      className={classes.textField}
                      value={this.state.lastName}
                      onChange={this.handleChange("lastName")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      id="standard-address"
                      variant="outlined"
                      label="Address"
                      className={classes.textField}
                      value={this.state.address}
                      onChange={this.handleChange("address")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      required
                      id="standard-password-input"
                      variant="outlined"
                      label="Password"
                      className={classes.textField}
                      type="password"
                      value={this.state.currentPassword}
                      //autoComplete="current-password"
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      id="standard-city-"
                      variant="outlined"
                      label="City"
                      className={classes.textField}
                      value={this.state.city}
                      onChange={this.handleChange("city")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      required
                      id="standard-confirmPassword-input"
                      variant="outlined"
                      label="Confirm Password"
                      className={classes.textField}
                      type="password"
                      value={this.state.confirmPassword}
                      //autoComplete="current-password"
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      id="standard-zipCode"
                      variant="outlined"
                      label="Zip Code"
                      className={classes.textField}
                      value={this.state.zipCode}
                      onChange={this.handleChange("zipCode")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid direction="column">
                    <TextField
                      id="standard-npi"
                      variant="outlined"
                      label="NPI Number"
                      className={classes.textField}
                      value={this.state.npi}
                      onChange={this.handleChange("npi")}
                      margin="normal"
                    />
                  </Grid>
                  <Grid direction="column">
                    <TextField
                      id="standard-primary-location"
                      variant="outlined"
                      label="Primary Location"
                      className={classes.textField}
                      value={this.state.primaryLocation}
                      onChange={this.handleChange("primaryLocation")}
                      margin="normal"
                    />
                  </Grid>

                  <TextField
                    id="standard-full-width"
                    label="Additional Notes"
                    style={{ margin: 8 }}
                    className={classes.textField2}
                    placeholder="Add any additional notes here"
                    fullWidth
                    multiline
                    margin="normal"
                    InputLabelProps={{
                      shrink: true
                    }}
                  />
                </Grid>
                <Grid container direction="row" justify="flex-end">
                  <Button
                    className={classes.saveButton}
                    size="large"
                    variant="contained"
                  >
                    Save
                  </Button>
                </Grid>
              </Paper>
            </form>
          )}
          {tabValue === 1 && (
            <Paper className={classes.root} elevation={7}>
              {/* Assign client button */}
              <Button
                variant="contained"
                onClick={this.handleClickOpen}
                className={classes.button}
              >
                <AddIcon
                  className={classNames(classes.leftIcon, classes.iconSmall)}
                />
                Assign Client
              </Button>
              <Dialog open={this.state.open} onClose={this.handleClose}>
                <DialogTitle id="form-dialog-title">Assign Client</DialogTitle>
                <DialogContent>
                  <TextField
                    id="standard-select-client"
                    select
                    label="Clients"
                    variant="outlined"
                    margin="normal"
                    className={classes.textField}
                    value={this.state.clients}
                    onChange={this.handleChange("clients")}
                    SelectProps={{
                      MenuProps: {
                        className: classes.menu
                      }
                    }}
                  >
                    {clientData.map(option => (
                      <MenuItem
                        key={option.value}
                        value={option.client_full_name}
                      >
                        {option.client_full_name}
                      </MenuItem>
                    ))}
                  </TextField>
                </DialogContent>
                <DialogActions>
                  <Button onClick={this.handleClose} color="primary">
                    Cancel
                  </Button>
                  <Button onClick={this.handleClose} color="primary">
                    Assign
                  </Button>
                </DialogActions>
              </Dialog>
            </Paper>
          )}
          {tabValue === 1 && <AssignedClientsTable />}
          {tabValue === 2 && <Paper>Message History</Paper>}
          {tabValue === 3 && <MemberUploadAction />}
          {tabValue === 3 && <TeamMemFilesTable />}
          {tabValue === 4 && (
            <Paper className={classes.root}>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.invoice}
                      onChange={this.handleChange3("invoice")}
                      value="invoice"
                    />
                  }
                  label="Invoices/Billing"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.allCalendar}
                      onChange={this.handleChange3("allCalendar")}
                      value="allCalendar"
                    />
                  }
                  label="Calendar of everyone"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.payment}
                      onChange={this.handleChange3("payment")}
                      value="payment"
                    />
                  }
                  label="Take payment"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.editOtherSched}
                      onChange={this.handleChange3("editOtherSched")}
                      value="editOtherSched"
                    />
                  }
                  label="Edit other therapist schedule"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.viewOtherSched}
                      onChange={this.handleChange3("viewOtherSched")}
                      value="viewOtherSched"
                    />
                  }
                  label="View other therapist schedule"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.editOtherInfo}
                      onChange={this.handleChange3("editOtherInfo")}
                      value="editOtherInfo"
                    />
                  }
                  label="Edit other therapist/client info"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.viewOtherInfo}
                      onChange={this.handleChange3("viewOtherInfo")}
                      value="viewOtherInfo"
                    />
                  }
                  label="View other therapist/client info"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.addClient}
                      onChange={this.handleChange3("addClient")}
                      value="addClient"
                    />
                  }
                  label="Add client"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.recordAttendance}
                      onChange={this.handleChange3("recordAttendance")}
                      value="recordAttendance"
                    />
                  }
                  label="Record Attendance"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.viewNote}
                      onChange={this.handleChange3("viewNote")}
                      value="viewNote"
                    />
                  }
                  label="View note history"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.viewClient}
                      onChange={this.handleChange3("viewClient")}
                      value="viewClient"
                    />
                  }
                  label="View other clients vs. own clients"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.kpis}
                      onChange={this.handleChange3("kpis")}
                      value="kpis"
                    />
                  }
                  label="KPIs"
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.viewReport}
                      onChange={this.handleChange3("viewReport")}
                      value="jason"
                    />
                  }
                  label="View/create reports"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.viewCalendar}
                      onChange={this.handleChange3("viewCalendar")}
                      value="viewCalendar"
                    />
                  }
                  label="View own calendar"
                />
                <FormControlLabel
                  control={
                    <Switch
                      disabled
                      checked={this.state.viewOwnClient}
                      onChange={this.handleChange3("viewOwnClient")}
                      value="viewOwnClient"
                    />
                  }
                  label="View own clients"
                />
              </FormGroup>
            </Paper>
          )}
        </Container>
      </div>
    );
  }
}

