import React, { Component } from "react";
//import { render } from "react-dom";
import events from "./events";
import BigCalendar from "react-big-calendar";
import moment from "moment";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = BigCalendar.momentLocalizer(moment);
const propTypes = {};

const styles = theme => ({
  state: {
    open: false,
    openV: false,
    client: "",
    therapist: "",
    sessionType: ""
  },

  textField2: {
    //marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200
  },
  textField3: {
    //marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200
  }
});

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const sessionTypes = [
  {
    value: "Lessons",
    label: "Lessons"
  },

  {
    value: "Therapy",
    label: "Therapy"
  }
];

class ReactCalendarBase extends Component {
  constructor(...args) {
    super(...args);

    this.state = { events };
  }

  /* show new event dialog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close new event dialog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  /* show existing event dialog box */
  handleClickOpen2 = () => {
    this.setState({ openV: true });
  };

  /* close existing event dialog box */
  handleClose2 = () => {
    this.setState({ openV: false });
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;

    return (
      <div style={{ height: 600 }}>
        <BigCalendar
          selectable
          localizer={localizer}
          events={events}
          views={["month", "week", "day"]}
          defaultDate={new Date(2019, 3, 1)}
          onSelectEvent={this.handleClickOpen2}
          onSelectSlot={this.handleClickOpen}
        />
        {/* existing dialog */}
        <Dialog open={this.state.openV} onClose={this.handleClose2}>
          <DialogTitle id="form-dialog-title">Existing Event</DialogTitle>
          <DialogContent>
            <TextField
              id="standard-date"
              label="title"
              variant="outlined"
              className={classes.textField2}
              value={events => events.title}
              //onChange={this.handleChange("date")}
              margin="normal"
            />
            <TextField
              id="standard-payor"
              label="start"
              variant="outlined"
              className={classes.textField2}
              value={events.start}
              //onChange={this.handleChange("payor")}
              margin="normal"
            />
            <TextField
              id="standard-amount"
              label="end"
              variant="outlined"
              className={classes.textField2}
              value={events.end}
              //onChange={this.handleChange("amount")}
              margin="normal"
            />
            <TextField
              id="standard-paymentMethod"
              label="Payment Method"
              variant="outlined"
              className={classes.textField2}
              //value={this.state.paymentMethod}
              //onChange={this.handleChange("paymentMethod")}
              margin="normal"
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose2} color="primary">
              Cancel
            </Button>
            <Button onClick={this.handleClose2} color="primary">
              Save
            </Button>
          </DialogActions>
        </Dialog>

        <Dialog open={this.state.open} onClose={this.handleClose}>
          <DialogTitle id="form-dialog-title">New Event</DialogTitle>
          <DialogContent>
            <TextField
              id="standard-select-client"
              select
              label="Client"
              variant="outlined"
              margin="normal"
              className={classes.textField2}
              value={this.state.client}
              onChange={this.handleChange("client")}
              SelectProps={{
                MenuProps: {
                  className: classes.menu
                }
              }}
            >
              {clients.map(option => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
            <TextField
              id="standard-select-client"
              select
              label="Therapist"
              variant="outlined"
              margin="normal"
              className={classes.textField2}
              value={this.state.therapist}
              onChange={this.handleChange("therapist")}
              SelectProps={{
                MenuProps: {
                  className: classes.menu
                }
              }}
            >
              {therapists.map(option => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
            <TextField
              id="standard-select-client"
              select
              label="Session Type"
              variant="outlined"
              margin="normal"
              className={classes.textField2}
              value={this.state.sessionType}
              onChange={this.handleChange("sessionType")}
              SelectProps={{
                MenuProps: {
                  className: classes.menu
                }
              }}
            >
              {sessionTypes.map(option => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
            <TextField
              id="standard-paymentMethod"
              label="Start"
              variant="outlined"
              className={classes.textField2}
              value={events.start}
              //onChange={this.handleChange("paymentMethod")}
              margin="normal"
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={this.handleClose} color="primary">
              Save
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

ReactCalendarBase.propTypes = propTypes;

//export default ReactCalendarBase;
export default withStyles(styles)(ReactCalendarBase);
