import React, { Component } from "react";
//import { render } from "react-dom";
import events from "./events";
import BigCalendar from "react-big-calendar";
import moment from "moment";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import NewEventDialog from "./newEventDialog";
import NewEventDialog2 from "./newEventDialog2";
import ExistingEventDialog from "./existingEventDialog";
import "react-big-calendar/lib/css/react-big-calendar.css";

import axios from "axios";
//tes

const localizer = BigCalendar.momentLocalizer(moment);
const propTypes = {};

const styles = theme => ({
  state: {
    open: false,
    openV: false
  },

  root: {
    padding: theme.spacing(10, 10),
    height: "100%",
    width: "90%"
  },
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400
  },
  textField2: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 192
  },
  dense: {
    marginTop: 16
  },
  menu: {
    width: 200
  }
});

const billTypes = [
  {
    value: "Billable",
    label: "Billable"
  },

  {
    value: "Non-billable",
    label: "Non-billable"
  }
];

const clientTypes = [
  {
    value: "Individual",
    label: "Individual"
  },

  {
    value: "Facility",
    label: "Facility"
  }
];

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const categories = [
  {
    value: "None",
    label: "None"
  }
];

class ReactCalendarBase extends Component {
  constructor(...args) {
    super(...args);

    this.state = {
      cal_events: [
        //State is updated via componentDidMount
      ],
      data: [],
      id: 0,
      billType: null,
      clientType: null,
      title: null,
      client: null,
      therapist: null,
      location: null,
      category: null,
      startDate: null,
      startTime: null,
      endDate: null,
      endTime: null,
      information: null,
      intervalIsSet: false,
      idToDelete: null,
      idToUpdate: null,
      objectToUpdate: null
    };
  }

  convertDate = date => {
    return moment.utc(date).toDate();
  };

  componentDidMount() {
    axios
      .get("http://localhost:5000/events")
      .then(response => {
        console.log(response.data);
        let appointments = response.data;

        for (let i = 0; i < appointments.length; i++) {
          appointments[i].start = this.convertDate(appointments[i].start);
          appointments[i].end = this.convertDate(appointments[i].end);
        }

        this.setState({
          cal_events: appointments
        });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  /* show new event dialog box */
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  /* close new event dialog box */
  handleClose = () => {
    this.setState({ open: false });
  };

  /* show existing event dialog box */
  handleClickOpen2 = () => {
    this.setState({ openV: true });
  };

  /* close existing event dialog box */
  handleClose2 = () => {
    this.setState({ openV: false });
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;
    //const classes = withStyles();
    const { cal_events } = this.state;

    return (
      <div style={{ height: 1000 }}>
        <BigCalendar
          className={classes.root}
          selectable
          localizer={localizer}
          events={cal_events}
          //events={events}
          views={["month", "week", "day"]}
          defaultDate={new Date(2019, 3, 1)}
          onSelectEvent={this.handleClickOpen2}
          onSelectSlot={this.handleClickOpen}
        />

        {/* existing dialog */}
        <Dialog open={this.state.openV} onClose={this.handleClose2}>
          <DialogContent>
            <ExistingEventDialog />
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose2} color="primary">
              Cancel
            </Button>
            <Button onClick={this.handleClose2} color="primary">
              Save
            </Button>
            <Button onClick={this.handleClose2} color="primary">
              Save & Next
            </Button>
          </DialogActions>
        </Dialog>

        <Dialog open={this.state.open} onClose={this.handleClose}>
          <form className={classes.container} noValidate autoComplete="off">
            <DialogContent>
              <TextField
                required
                id="bill_type"
                select
                label="Bill Type"
                className={classes.textField}
                value={this.state.billType}
                onChange={this.handleChange("billType")}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {billTypes.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>

              <TextField
                required
                id="client_type"
                select
                label="Client Type"
                className={classes.textField}
                value={this.state.clientType}
                onChange={this.handleChange("")}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {clientTypes.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="client"
                select
                label="Client"
                className={classes.textField}
                value={this.state.client}
                onChange={this.handleChange("client")}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {clients.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="therapist"
                select
                label="Therapist"
                className={classes.textField}
                value={this.state.therapist}
                onChange={this.handleChange("client")}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {therapists.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
              <TextField
                required
                id="location"
                label="Location "
                className={classes.textField2}
                value={this.state.location}
                onChange={this.handleChange("location")}
                margin="normal"
                variant="outlined"
              />
              <TextField
                required
                id="category"
                select
                label="Category"
                className={classes.textField2}
                value={this.state.category}
                onChange={this.handleChange("category")}
                //helperText="Please select your currency"
                margin="normal"
                variant="outlined"
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {categories.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
            </DialogContent>
            <DialogActions>
              <Button onClick={this.handleClose} color="primary">
                Cancel
              </Button>
              <Button onClick={this.handleClose} color="primary">
                Save
              </Button>
            </DialogActions>
          </form>
        </Dialog>
      </div>
    );
  }
}

ReactCalendarBase.propTypes = propTypes;

//export default ReactCalendarBase;
export default withStyles(styles)(ReactCalendarBase);
