import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import "moment";
import MomentUtils from "@date-io/moment";
import {
  MuiPickersUtilsProvider,
  DatePicker,
  TimePicker
} from "@material-ui/pickers";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormLabel from "@material-ui/core/FormLabel";
import FormControl from "@material-ui/core/FormControl";
import Checkbox from "@material-ui/core/Checkbox";

const clients = [
  {
    value: "John Smith",
    label: "John Smith"
  },

  {
    value: "Jill Smith",
    label: "Jill Smith"
  },
  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  }
];

const therapists = [
  {
    value: "Therapist 1",
    label: "Therapist 1"
  },

  {
    value: "Harry Potter",
    label: "Harry Potter"
  },
  {
    value: "Therapist 3",
    label: "Therapist 3"
  }
];

const categories = [
  {
    value: "None",
    label: "None"
  }
];

const repeatOptions = [
  {
    value: "Daily",
    label: "Daily"
  },

  {
    value: "Weekly",
    label: "Weekly"
  },
  {
    value: "Monthly",
    label: "Monthly"
  },
  {
    value: "Custom",
    label: "Custom"
  }
];

const repeatEndOptions = [
  {
    value: "Never",
    label: "Never"
  },

  {
    value: "After",
    label: "After"
  },
  {
    value: "On Date",
    label: "On Date"
  }
];

const customFreqOptions = [
  {
    value: "Daily",
    label: "Daily"
  },

  {
    value: "Weekly",
    label: "Weekly"
  },
  {
    value: "Monthly",
    label: "Monthly"
  }
];

const useStyles = makeStyles(theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400
  },
  textField2: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 192
  },
  rootCustWeekDay: {
    display: "flex"
  },
  formControlCustWeekDay: {
    //margin: theme.spacing(),
  }
}));

export default function OutlinedTextFields() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    newClient: "",
    newTherapist: "",
    newLocation: "",
    newCategory: "",
    newRepeat: "",
    endNewRepeat: "",
    newNumOccurrences: "",
    newCustomFreq: "",
    newEveryNumDays: "",
    newEveryNumWeeks: ""
  });
  const [state, setState] = React.useState({
    checkedRepeat: true,
    mon: false,
    tues: false,
    wed: false,
    thu: false,
    fri: false,
    sat: false,
    sun: false
  });

  // The first commit of Material-UI
  const [selectedDateStart, setSelectedDateStart] = React.useState(
    new Date("2019-04-01T21:11:54")
  );

  const [selectedDateEnd, setSelectedDateEnd] = React.useState(
    new Date("2019-04-01T21:11:54")
  );

  const [selectedTimeStart, setSelectedTimeStart] = React.useState(
    new Date("2019-04-01T15:11:54")
  );
  const [selectedTimeEnd, setSelectedTimeEnd] = React.useState(
    new Date("2019-05-01T21:11:54")
  );

  const [
    selectedDateOccurenceEnd,
    setSelectedDateOccurenceEnd
  ] = React.useState(new Date("2019-05-01T21:11:54"));

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  const handleChangeRepeat = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  const handleChangeCustomFreq = name => event => {
    setValues({ ...values, [name]: event.target.value });
