import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import classNames from "classnames";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 300
  },

  root: {
    width: "77.5%",
    marginLeft: theme.spacing.unit * 22,
    paddingTop: theme.spacing.unit,
    paddingBottom: theme.spacing.unit * 2
  },

  textField2: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit * 11,
    marginTop: theme.spacing.unit,
    width: 200
  },

  button: {
    marginLeft: theme.spacing(2)
  },

  saveButton: {
    marginRight: theme.spacing(2)
  }
});

class TopPanel extends React.Component {
  state = {
    nameOnCard: "Jeff A. Smith",
    cardNum: "111122223333",
    email: "jeffsmith@mail.com",
    title: "Mr.",
    paymentType: "Card",
    billingPhone: "123-456-1111",
    address: "123 Maple Street",
    city: "Plano",
    zipCode: "75023",
    st: "TX",
    cvv: "000",
    expDate: "03/17",
    cardType: "Visa",
    sameAsContact: false
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  handleChange2 = name => event => {
    this.setState({ [name]: event.target.value });
  };
  handleChange3 = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;

    return (
      <form className={classes.container} noValidate autoComplete="off">
        <Paper className={classes.root} elevation={1}>
          <Grid
            container
            direction="column"
            justify="center"
            alignItems="center"
          >
            <TextField
              id="standard-address"
              //variant="outlined"
              label="Goal"
              className={classes.textField}
              value={this.state.address}
              onChange={this.handleChange("address")}
              margin="normal"
            />

            <TextField
              id="standard-nameOnCard"
              label="Objective"
              //variant="outlined"
              className={classes.textField2}
              value={this.state.nameOnCard}
              onChange={this.handleChange("nameOnCard")}
              margin="normal"
            />
          </Grid>
          <Grid container direction="row" justify="space-between">
            <Button
              size="medium"
              variant="contained"
              className={classes.button}
            >
              +
            </Button>
            <Button
              className={classes.saveButton}
              size="large"
              variant="contained"
            >
              Save
            </Button>
          </Grid>
        </Paper>
      </form>
    );
  }
}

TopPanel.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(TopPanel);
