import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import {
  BrowserRouter,
  Switch,
  Link,
  Route,
  withRouter,
  Redirect
} from "react-router-dom";
//test

import axios from "axios";

const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 300
  },

  menu: {
    width: 200
  },
  root: {
    width: "100%",
    //marginLeft: theme.spacing.unit * 22,
    paddingTop: theme.spacing.unit,
    paddingBottom: theme.spacing.unit * 2
  },

  container2: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField2: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    // marginBottom: theme.spacing(10),
    width: 850
  },
  title: {
    align: "center",
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1)
  },
  title2: {
    align: "center",
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(2)
  },

  menu2: {
    width: 200
  },
  root2: {
    width: 1020,
    paddingBottom: theme.spacing.unit * 2
  },

  saveButton: {
    marginRight: theme.spacing(2),
    align: "center"
  }
});

const titles = [
  {
    value: "",
    label: ""
  },

  {
    value: "Dr.",
    label: "Dr."
  },

  {
    value: "Miss",
    label: "Miss"
  },

  {
    value: "Mr.",
    label: "Mr."
  },
  {
    value: "Mrs.",
    label: "Mrs."
  },
  {
    value: "Ms.",
    label: "Ms."
  },
  {
    value: "Mx.",
    label: "Mx."
  }
];

const sessionTypes = [
  {
    value: "Lessons",
    label: "Lessons"
  },

  {
    value: "Therapy",
    label: "Therapy"
  }
];

const paymentTypes = [
  {
    value: "Card",
    label: "Card"
  },

  {
    value: "Cash",
    label: "Cash"
  },

  {
    value: "Check",
    label: "Check"
  }
];

const cardTypes = [
  {
    value: "American Express",
    label: "American Express"
  },

  {
    value: "Discover",
    label: "Discover"
  },

  {
    value: "MasterCard",
    label: "MasterCard"
  },
  {
    value: "Visa",
    label: "Visa"
  }
];

const navStyle = {
  color: "black",
  textDecoration: "none"
};

class TopPanel extends React.Component {
  state = {
    //client info state
    clientFirstName: "",
    clientLastName: "",
    clientEmail: "",
    clientTitle: "",
    clientCurrentPassword: "",
    clientConfirmPassword: "",
    sessionType: "",
    sessionCost: "",
    sessionLegnth: "",
    clientPhone: "",
    clientAddress: "",
    clientBday: "",
    clientState: "",
    clientCity: "",
    clientZipCode: "",
    clientStreetAddress: "",
    clientNotes: "",
    multiline: "Controlled",
    //contact info state
    contactFirstName: "",
    contactLastName: "",
    contactEmail: "",
    contactTitle: "",
    contactPhone: "",
    contactAddress: "",
    contactCity: "",
    contactState: "",
    contactZip: "",
    //payor info state
    nameOnCard: "",
    cardNum: "",
    payorEmail: "",
    payorTitle: "",
    paymentType: "",
    billingPhone: "",
    billingAddress: "",
    billingCity: "",
    billingZip: "",
    billingState: "",
    cvv: "",
    expDate: "",
    cardType: "",
    sameAsContact: false,
    //toDashboard: false,
    navigate: false,
    //intervalIsSet: ""
    memberData: [],
    therapist: ""
  };

  reloadPage() {
    // if (this.state.location !== prevState.location) {
    window.location.reload();
    console.log("Refresh!");
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/allclients")
      .then(response => {
        console.log("Got client data!");
        console.log(response.data);
        this.setState({
          clientData: response.data
        });
      })
      .then(response2 => {
        return axios
          .get("http://localhost:5000/members") // using response.data
          .then(response2 => {
            console.log("Got member data!");
            console.log(response2.data);
            this.setState({
              memberData: response2.data
            });
          });
      })
      .catch(function(error) {
        console.log(error);
      });

    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
      console.log("Data interval set!");
    }
  }

  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
      console.log("Unmounted from client data!");
    }
  }

  onSubmit() {
    //experiment keeping preventDefault
    //e.preventDefault();

    const clientObj = {
      // client info
      clientFirstName: this.state.clientFirstName,
      clientLastName: this.state.clientLastName,
      clientEmail: this.state.clientEmail,
      clientTitle: this.state.clientTitle,
      clientCurrentPassword: this.state.clientCurrentPassword,
      clientConfirmPassword: this.state.clientConfirmPassword,
      clientPhone: this.state.clientPhone,
      clientAddress: this.state.clientAddress,
      sessionType: this.state.sessionType,
      sessionCost: this.state.sessionCost,
      sessionLength: this.state.sessionLegnth,
      //primaryLocation: this.state.primaryLocation,
      clientBday: this.state.clientBday,
      clientState: this.state.clientState,
      clientCity: this.state.clientCity,
      clientZipCode: this.state.clientZipCode,
      clientStreetAddress: this.state.clientStreetAddress,
      clientNotes: this.state.clientNotes,
      // contact info
      contactFirstName: this.state.contactFirstName,
      contactLastName: this.state.contactLastName,
      contactEmail: this.state.contactEmail,
      contactTitle: this.state.contactTitle,
      contactPhone: this.state.contactPhone,
      contactAddress: this.state.contactAddress,
      contactCity: this.state.contactCity,
      contactState: this.state.contactState,
      contactZip: this.state.contactZip,
      // payor info
      nameOnCard: this.state.nameOnCard,
      cardNum: this.state.cardNum,
      payorEmail: this.state.payorEmail,
      payorTitle: this.state.payorTitle,
      paymentType: this.state.paymentType,
      billingPhone: this.state.billingPhone,
      billingAddress: this.state.billingAddress,
      billingCity: this.state.billingCity,
      billingZip: this.state.billingZip,
      billingState: this.state.billingState,
      cvv: this.state.cvv,
      expDate: this.state.expDate,
      cardType: this.state.cardType
    };
    axios
      .post("http://localhost:5000/clients/registerclient", clientObj)
      .then(res => console.log(res.data));
    /* this will clear everything after saving+closing */

    this.setState({
      //client info
      clientObj
    });
  }

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  handleChange2 = name => event => {
    this.setState({ [name]: event.target.value });
  };
  /*
  handleSubmit = (user) => {
    saveUser(user)
      .then(() => this.setState(() => ({
        toDashboard: true
      })))
  }
  */

  setLocation() {
    this.props.history.push("/clients");
  }

  render() {
    const { classes } = this.props;
    const { memberData } = this.state;
    /*
    if (this.state.toDashboard === true) {
        return <Redirect to='/dashboard' />
      }
  */

    const { navigate } = this.state;

    // here is the important part
    if (navigate) {
      return <Redirect to="/clients" push={true} />;
    }

    return (
      <BrowserRouter>
        <Container maxWidth="lg">
          <form className={classes.container} noValidate autoComplete="off">
            <Paper className={classes.root} elevation={1}>
              <Typography className={classes.title} align="center" variant="h6">
                Client Information
              </Typography>
              <Grid container justify="center" alignItems="center">
                <TextField
                  id="standard-select-title"
                  select
                  label="Title"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.clientTitle}
                  onChange={this.handleChange("clientTitle")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {titles.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
                <TextField
                  required
                  id="standard-firstName"
                  label="First Name"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.clientFirstName}
                  onChange={this.handleChange("clientFirstName")}
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-lastNamename"
                  label="Last Name"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.clientLastName}
                  onChange={this.handleChange("clientLastName")}
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-email"
                  variant="outlined"
                  label="Email"
                  className={classes.textField}
                  value={this.state.clientEmail}
                  onChange={this.handleChange("clientEmail")}
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-password-input"
                  variant="outlined"
                  label="Password"
                  className={classes.textField}
                  type="password"
                  value={this.state.clientCurrentPassword}
                  onChange={this.handleChange("clientCurrentPassword")}
                  //autoComplete="current-password"
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-confirmPassword-input"
                  variant="outlined"
                  label="Confirm Password"
                  className={classes.textField}
                  type="password"
                  value={this.state.confirmPassword}
                  onChange={this.handleChange("confirmPassword")}
                  //autoComplete="current-password"
                  margin="normal"
                />
                <TextField
                  id="standard-select-sessionType"
                  select
                  label="Session Type"
                  variant="outlined"
                  margin="normal"
                  className={classes.textField}
                  value={this.state.sessionType}
                  onChange={this.handleChange("sessionType")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {sessionTypes.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
                <TextField
                  id="standard-cost"
                  label="Session Cost (dollars)"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.sessionCost}
                  onChange={this.handleChange("sessionCost")}
                  margin="normal"
                />
                <TextField
                  id="standard-length"
                  label="Session Length (minutes)"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.sessionLength}
                  onChange={this.handleChange("sessionLength")}
                  margin="normal"
                />
                <TextField
                  id="standard-select-therapist"
                  select
                  label="Therapist"
                  variant="outlined"
                  margin="normal"
                  className={classes.textField}
                  value={this.state.therapist}
                  onChange={this.handleChange("therapist")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {memberData.map(option => (
                    <MenuItem
                      key={option.value}
                      value={/* option.first_name */ option.last_name}
                    >
                      {option.first_name} {option.last_name}
                    </MenuItem>
                  ))}
                </TextField>
                <TextField
                  id="standard-address"
                  variant="outlined"
                  label="Address"
                  className={classes.textField}
                  value={this.state.clientAddress}
                  onChange={this.handleChange("clientAddress")}
                  margin="normal"
                />
                <TextField
                  id="standard-city-"
                  variant="outlined"
                  label="City"
                  className={classes.textField}
                  value={this.state.clientCity}
                  onChange={this.handleChange("clientCity")}
                  margin="normal"
                />
                <TextField
                  id="standard-state"
                  variant="outlined"
                  label="State"
                  className={classes.textField}
                  value={this.state.clientState}
                  onChange={this.handleChange("clientState")}
                  margin="normal"
                />
                <TextField
                  id="standard-zipCode"
                  variant="outlined"
                  label="Zip Code"
                  className={classes.textField}
                  value={this.state.clientZipCode}
                  onChange={this.handleChange("clientZipCode")}
                  margin="normal"
                />
                <TextField
                  id="standard-phone"
                  variant="outlined"
                  label="Phone Number"
                  className={classes.textField}
                  value={this.state.clientPhone}
                  onChange={this.handleChange("clientPhone")}
                  margin="normal"
                />
                <TextField
                  id="standard-bday"
                  variant="outlined"
                  label="Birthday"
                  className={classes.textField}
                  value={this.state.clientBday}
                  onChange={this.handleChange("clientBday")}
                  margin="normal"
                />

                <TextField
                  id="standard-full-width"
                  label="Additional Notes"
                  style={{ margin: 8 }}
                  className={classes.textField2}
                  value={this.state.clientNotes}
                  onChange={this.handleChange("clientNotes")}
                  placeholder="Add any additional notes here"
                  fullWidth
                  multiline
                  margin="normal"
                  InputLabelProps={{
                    shrink: true
                  }}
                />
                <Container>
                  <Typography
                    className={classes.title}
                    align="center"
                    variant="h6"
                  >
                    Contact Information
                  </Typography>
                </Container>
                {/* Contact Info*/}
                <TextField
                  id="standard-select-contactTitle"
                  select
                  label="Title"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.contactTitle}
                  onChange={this.handleChange("contactTitle")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {titles.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
                <TextField
                  required
                  id="standard-contactFirstName"
                  label="First Name"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.contactFirstName}
                  onChange={this.handleChange("contactFirstName")}
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-lastNamename"
                  label="Last Name"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.contactLastName}
                  onChange={this.handleChange("contactLastName")}
                  margin="normal"
                />
                <TextField
                  id="standard-contactAddress"
                  variant="outlined"
                  label="Street Address"
                  className={classes.textField}
                  value={this.state.contactAddress}
                  onChange={this.handleChange("contactAddress")}
                  margin="normal"
                />
                <TextField
                  id="standard-contactCity-"
                  variant="outlined"
                  label="City"
                  className={classes.textField}
                  value={this.state.contactCity}
                  onChange={this.handleChange("contactCity")}
                  margin="normal"
                />
                <TextField
                  id="standard-state"
                  variant="outlined"
                  label="State"
                  className={classes.textField}
                  value={this.state.contactState}
                  onChange={this.handleChange("contactState")}
                  margin="normal"
                />
                <TextField
                  id="standard-contactZip"
                  variant="outlined"
                  label="Zip Code"
                  className={classes.textField}
                  value={this.state.contactZip}
                  onChange={this.handleChange("contactZip")}
                  margin="normal"
                />
                <TextField
                  required
                  id="standard-contactEmail"
                  variant="outlined"
                  label="Email"
                  className={classes.textField}
                  value={this.state.contactEmail}
                  onChange={this.handleChange("contactEmail")}
                  margin="normal"
                />
                <TextField
                  id="standard-contactPhone"
                  variant="outlined"
                  label="Phone Number"
                  className={classes.textField}
                  value={this.state.contactPhone}
                  onChange={this.handleChange("contactPhone")}
                  margin="normal"
                />
                {/*
            <Button
              size="medium"
              variant="contained"
              className={classes.button}
            >
              Add Additional
            </Button>
            */}

                {/* Payor Info  */}
                {/*
              <FormGroup className={classes.marg} row>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={this.state.sameAsContact}
                      onChange={this.handleChange("sameAsContact")}
                      value="sameAsContact"
                      color="default"
                    />
                  }
                  label="Same as contact" 
                />
              </FormGroup>
                */}
                <Container>
                  <Typography
                    className={classes.title2}
                    align="center"
                    variant="h6"
                  >
                    Payor Information
                  </Typography>
                </Container>
                <Grid container justify="center" alignItems="center">
                  <TextField
                    id="standard-select-paymentType"
                    select
                    label="Payment Type"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.paymentType}
                    onChange={this.handleChange("paymentType")}
                    SelectProps={{
                      MenuProps: {
                        className: classes.menu
                      }
                    }}
                  >
                    {paymentTypes.map(option => (
                      <MenuItem key={option.value} value={option.value}>
                        {option.label}
                      </MenuItem>
                    ))}
                  </TextField>

                  <TextField
                    id="standard-select-cardType"
                    select
                    label="Card Type"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.cardType}
                    onChange={this.handleChange("cardType")}
                    SelectProps={{
                      MenuProps: {
                        className: classes.menu
                      }
                    }}
                  >
                    {cardTypes.map(option => (
                      <MenuItem key={option.value} value={option.value}>
                        {option.label}
                      </MenuItem>
                    ))}
                  </TextField>

                  {/*

                <TextField
                  id="standard-select-payorTitle"
                  select
                  label="payorTitle"
                  variant="outlined"
                  className={classes.textField}
                  value={this.state.payorTitle}
                  onChange={this.handleChange("payorTitle")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {titles.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
                  */}

                  <TextField
                    required
                    id="standard-nameOnCard"
                    label="Name on Card"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.nameOnCard}
                    onChange={this.handleChange("nameOnCard")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-billingAddress"
                    variant="outlined"
                    label="Billing Street Address"
                    className={classes.textField}
                    value={this.state.billingAddress}
                    onChange={this.handleChange("billingAddress")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-cardNum"
                    label="Card Number"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.cardNum}
                    onChange={this.handleChange("cardNum")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-billingCity-"
                    variant="outlined"
                    label="City"
                    className={classes.textField}
                    value={this.state.billingCity}
                    onChange={this.handleChange("billingCity")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-payorEmail"
                    variant="outlined"
                    label="Email"
                    className={classes.textField}
