import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import deepPurple from "@material-ui/core/colors/deepPurple";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import green from "@material-ui/core/colors/green";
import red from "@material-ui/core/colors/red";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Checkbox from "@material-ui/core/Checkbox";
import AppBar from "@material-ui/core/AppBar";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import Button from "@material-ui/core/Button";
import AssignedClientsTable from "../Tables/assignedClientsTable";

import TeamMemFilesTable from "../Tables/teamMemFilesTable";
import AssignedClientsActions from "../Actions/asssignedClientsActions";
import PleaseWork3 from "../teamMember/teamMemberInfo";
import ClientInfo from "./clientInfo";
import ClientContactInfo from "./clientContactInfo";
import ClientPayorInfo from "./clientPayorInfo";
import NoteHistoryTableOld from "../Old/clientNoteHistoryTableOld";
import NoteHistoryTable from "../Tables/noteHistoryTable";
import ClientAvatar from "./clientAvatarTest";
import GoalsObjectives from "./goalsObjectives";
import Cyan from "@material-ui/core/colors/cyan";
import Container from "@material-ui/core/Container";

const styles = theme => ({
  root: {
    //paddingTop: theme.spacing.unit * 1,
    // paddingBottom: theme.spacing.unit * 1,
    // paddingLeft: theme.spacing.unit,
    marginTop: theme.spacing.unit * 15,
    // marginLeft: theme.spacing.unit * 22,
    //marginRight: theme.spacing.unit * 22,
    align: "center",
    width: "100%",
    height: "120"
  },

  toggle: {
    paddingRight: theme.spacing.unit * 30
  },

  marg: {
    marginLeft: theme.spacing.unit,
    marginBottom: theme.spacing.unit
  },

  purpleAvatar: {
    margin: 10,
    color: "#fff",
    backgroundColor: deepPurple[500],
    width: 100,
    height: 100,
    fontSize: 40,
    marginBottom: theme.spacing.unit * -28,
    marginRight: theme.spacing.unit * 18
  },

  colorSwitchBase: {
    color: red[300],
    "&$colorChecked": {
      color: green[500],
      "& + $colorBar": {
        backgroundColor: green[500]
      }
    }
  },
  colorBar: {},
  colorChecked: {},

  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    //marginBottom: theme.spacing.unit,
    width: 300
  },

  textField2: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    marginBottom: theme.spacing.unit,
    width: 200
  },
  menu: {
    width: 200
  },

  checked: {},

  root2: {
    //flexGrow: 1,
    //backgroundColor: theme.palette.background.paper,
    //marginLeft: theme.spacing.unit * 22,
    //marginRight: theme.spacing.unit * 22,
    //marginTop: theme.spacing.unit * 22,
    width: "100%",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: Cyan[800]
  },

  infoRoot: {
    width: "100%",
    // marginLeft: theme.spacing.unit * 22,
    paddingTop: theme.spacing.unit,
    paddingBottom: theme.spacing.unit * 2
  }
});

const titles = [
  {
    value: "",
    label: ""
  },

  {
    value: "Dr.",
    label: "Dr."
  },

  {
    value: "Miss",
    label: "Miss"
  },

  {
    value: "Mr.",
    label: "Mr."
  },
  {
    value: "Mrs.",
    label: "Mrs."
  },
  {
    value: "Ms.",
    label: "Ms."
  },
  {
    value: "Mx.",
    label: "Mx."
  }
];

const selectedClient = [
  {
    value: "Billy Joe",
    label: "Billy Joe"
  },

  {
    value: "Ashley Flowers",
    label: "Ashley Flowers"
  },
  {
    value: "John Smith",
    label: "John Smith"
  }
];

const clientTypes = [
  {
    value: "",
    label: ""
  },

  {
    value: "Individual",
    label: "Individual"
  },

  {
    value: "Facility",
    label: "Facility"
  }
];

const paymentTypes = [
  {
    value: "Card",
    label: "Card"
  },

  {
    value: "Cash",
    label: "Cash"
  },

  {
    value: "Check",
    label: "Check"
  }
];

const cardTypes = [
  {
    value: "American Express",
    label: "American Express"
  },

  {
    value: "Discover",
    label: "Discover"
  },

  {
    value: "MasterCard",
    label: "MasterCard"
  },
  {
    value: "Visa",
    label: "Visa"
  }
];

class TopPanel extends React.Component {
  state = {
    checkedA: true,
    client: "John Smith",
    open: false,
    cost: "",
    length: "",
    clientType: "",
    checkedProspect: false,
    checkedThera: true,
    checkedIntern: false,
    tabValue: 0,
    //infotabs
    firstName: "John",
    lastName: "Smith",
    email: "jsmith@mail.com",
    title: "Mr.",
    currentPassword: "test123",
    confirmPassword: "test123",
    phone: "123-456-1111",
    address: "123 Maple Street",
    primaryLocation: "Main Office",
    bday: "2/20/1998",
    city: "Plano",
    zipCode: "75023",
    st: "TX",
    multiline: "Controlled",
    // CONTACT TAB
    contactFirstName: "John",
    contactLastName: "Smith",
    contactEmail: "jsmith@mail.com",
    contactAddress: "123 Maple Street",
    contactTitle: "Mr.",
    contactPhone: "123-456-1111",
    contactCity: "Plano",
    contactState: "TX",
    contactZip: "75023",
    // multiline: "Controlled"
    // PAYOR TAB
    nameOnCard: "Jeff A. Smith",
    cardNum: "111122223333",
    payorEmail: "jeffsmith@mail.com",
    payorTitle: "Mr.",
    paymentType: "Card",
    billingPhone: "123-456-1111",
    payorAddress: "123 Maple Street",
    payorCity: "Plano",
    payorZip: "75023",
    payorState: "TX",
    cvv: "000",
    expDate: "03/17",
    cardType: "Visa",
    sameAsContact: false
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.checked });
  };

  /* change of client dropdown */
  handleChange2 = name => event => {
    this.setState({ [name]: event.target.value });
  };

  // change of check boxes
  handleChange3 = name => event => {
    this.setState({ [name]: event.target.checked });
  };

  handleChange4 = (event, tabValue) => {
    this.setState({ tabValue });
  };

  render() {
    const { classes } = this.props;
    const { tabValue } = this.state;

    return (
      <div>
        <Container maxWidth="lg">
          {/* Avatar */}
          <Grid container justify="center" alignItems="center">
            <ClientAvatar />
          </Grid>
          <Paper className={classes.root} elevation={1}>
            <Grid container row justify="space-between">
              <FormGroup row>
                <FormControlLabel
                  control={
                    <Switch
                      checked={this.state.checkedA}
                      onChange={this.handleChange("checkedA")}
                      value="checkedA"
                      className={classes.marg}
                      classes={{
                        switchBase: classes.colorSwitchBase,
                        checked: classes.colorChecked,
                        bar: classes.colorBar
                      }}
                    />
                  }
                  label="Active"
                />
              </FormGroup>

              <FormGroup row>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={this.state.checkedProspect}
                      onChange={this.handleChange("checkedProspect")}
                      value="checkedProspect"
                      color="default"
                    />
                  }
                  label="Prospect"
                />
              </FormGroup>
            </Grid>
            {/* client drop down */}
            <Grid container row justify="space-between">
              <TextField
                id="standard-select-client"
                select
                label="Client"
                variant="outlined"
                className={classes.textField}
                value={this.state.client}
                onChange={this.handleChange("client")}
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {selectedClient.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>

              <TextField
                id="standard-select-member"
                select
                label="Client Type"
                variant="outlined"
                className={classes.textField2}
                value={this.state.clientType}
                onChange={this.handleChange("clientType")}
                SelectProps={{
                  MenuProps: {
                    className: classes.menu
                  }
                }}
              >
                {clientTypes.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
          </Paper>

          <AppBar className={classes.root2} position="static">
            <Tabs value={tabValue} onChange={this.handleChange4}>
              <Tab label="Client Information" />
              <Tab label="Contact Information" />
              <Tab label="Payor Information" />
              <Tab label="Note History" />
              <Tab label="Message History" />
              <Tab label="Goals & Objectives" />
            </Tabs>
          </AppBar>

          {tabValue === 0 && (
            <form
              /* className={classes.container} */ noValidate
              autoComplete="off"
            >
              <Paper /* className={classes.infoRoot} */ elevation={1}>
                <Grid container justify="center" alignItems="center">
                  <TextField
                    id="standard-select-title"
                    select
                    label="Title"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.title}
                    onChange={this.handleChange("title")}
                    SelectProps={{
                      MenuProps: {
                        className: classes.menu
                      }
                    }}
                  >
                    {titles.map(option => (
                      <MenuItem key={option.value} value={option.value}>
                        {option.label}
                      </MenuItem>
                    ))}
                  </TextField>

                  <TextField
                    id="standard-address"
                    variant="outlined"
                    label="Address"
                    className={classes.textField}
                    value={this.state.address}
                    onChange={this.handleChange("address")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-phone"
                    variant="outlined"
                    label="Phone Number"
                    className={classes.textField}
                    value={this.state.phone}
                    onChange={this.handleChange("phone")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-firstName"
                    label="First Name"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.firstName}
                    onChange={this.handleChange("firstName")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-city-"
                    variant="outlined"
                    label="City"
                    className={classes.textField}
                    value={this.state.city}
                    onChange={this.handleChange("city")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-email"
                    variant="outlined"
                    label="Email"
                    className={classes.textField}
                    value={this.state.email}
                    onChange={this.handleChange("email")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-lastNamename"
                    label="Last Name"
                    variant="outlined"
                    className={classes.textField}
                    value={this.state.lastName}
                    onChange={this.handleChange("lastName")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-state"
                    variant="outlined"
                    label="State"
                    className={classes.textField}
                    value={this.state.st}
                    onChange={this.handleChange("st")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-password-input"
                    variant="outlined"
                    label="Password"
                    className={classes.textField}
                    type="password"
                    value={this.state.currentPassword}
                    //autoComplete="current-password"
                    margin="normal"
                  />

                  <TextField
                    id="standard-bday"
                    variant="outlined"
                    label="Birthday"
                    className={classes.textField}
                    value={this.state.bday}
                    onChange={this.handleChange("bday")}
                    margin="normal"
                  />

                  <TextField
                    id="standard-zipCode"
                    variant="outlined"
                    label="Zip Code"
                    className={classes.textField}
                    value={this.state.zipCode}
                    onChange={this.handleChange("zipCode")}
                    margin="normal"
                  />

                  <TextField
                    required
                    id="standard-confirmPassword-input"
                    variant="outlined"
                    label="Confirm Password"
                    className={classes.textField}
                    type="password"
                    value={this.state.confirmPassword}
                    //autoComplete="current-password"
                    margin="normal"
                  />

                  <TextField
                    id="standard-full-width"
                    label="Additional Notes"
                    style={{ margin: 8 }}
                    className={classes.textField2}
                    placeholder="Add any additional notes here"
                    fullWidth
                    multiline
                    margin="normal"
                    InputLabelProps={{
                      shrink: true
                    }}
                  />

